export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: "삭은이",
      },
      content: "첫번째 게시글 #태그",
      Images: [
        {
          src: "https://m.londonslacks.com/web/product/big/201907/ece0ec037ccc8d580daaa51701b90b7a.jpg",
        },
        {
          src: "https://m.londonslacks.com/web/product/big/201907/ece0ec037ccc8d580daaa51701b90b7a.jpg",
        },
        // {
        //   src: "https://m.londonslacks.com/web/product/big/201907/ece0ec037ccc8d580daaa51701b90b7a.jpg",
        // },
      ],
      Comments: [
        {
          User: {
            nickname: "nero",
          },
          content: "우와",
        },
        {
          User: {
            nickname: "hero",
          },
          content: "빨리하고싶다",
        },
      ],
    },
  ],
  imagePaths: [],
  addPostLoading: false,
  addPostDone: false,
  addPostError: null,
};

export const ADD_POST_REQUEST = "ADD_POST_REQUEST";
export const ADD_POST_SUCCESS = "ADD_POST_SUCCESS";
export const ADD_POST_FAILURE = "ADD_POST_FAILURE";


export const ADD_COMMENT_REQUEST = "ADD_POST_REQUEST";
export const ADD_COMMENT_SUCCESS = "ADD_POST_SUCCESS";
export const ADD_COMMENT_FAILURE = "ADD_POST_FAILURE";
export const addPost = (data) => ({
  type: ADD_POST_REQUEST,
  data,
});

const dummyPost = {
  id: 2,
  User: {
    id: 1,
    nickname: "제로초",
  },
  Images: [],
  Comments: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST_REQUEST:
    case ADD_POST_SUCCESS:
      return {
        ...state,
        mainPosts: [dummyPost, ...state.mainPosts],
        postAdded: true,
      };
    case ADD_POST_FAILURE:
    default:
      return state;
  }
};

export default reducer;
