              key="heart"
              onCick={onToggleLike}
            />
          ) : (
            <HeartOutlined key="heart" />
          ),
          <MessageOutlined key="comment" />,
          <Popover
            key="more"
            content={
              <Button.Group>
                {id && post.User.id === id ? (
                  <>
                    <Button>수정</Button>
